import React, { useState } from 'react';
import '../App.css';
import { FaTrash, FaEdit, FaCheck, FaTimes } from 'react-icons/fa';
import '@fontsource/roboto'; // Google Font

function StreamList() {
  const [inputValue, setInputValue] = useState('');
  const [items, setItems] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editValue, setEditValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!inputValue.trim()) return;
    setItems([...items, { text: inputValue.trim(), completed: false }]);
    setInputValue('');
  };

  const handleDelete = (index) => {
    setItems(items.filter((_, i) => i !== index));
  };

  const handleComplete = (index) => {
    const newItems = [...items];
    newItems[index].completed = !newItems[index].completed;
    setItems(newItems);
  };

  const handleEdit = (index) => {
    setEditIndex(index);
    setEditValue(items[index].text);
  };

  const handleEditChange = (event) => {
    setEditValue(event.target.value);
  };

  const handleSaveEdit = (index) => {
    const updatedItems = [...items];
    updatedItems[index].text = editValue;
    setItems(updatedItems);
    setEditIndex(null);
  };

  return (
    <div className="page-container" style={{ fontFamily: 'Roboto, sans-serif' }}>
      <h1>Welcome to StreamList!</h1>
      <p>This is your homepage where you can add, edit, complete, or delete stream items.</p>

      <form onSubmit={handleSubmit} className="streamlist-form">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Enter a stream item..."
          className="streamlist-input"
        />
        <button type="submit" className="submit-button">Add</button>
      </form>

      <ul className="streamlist-items">
        {items.map((item, index) => (
          <li key={index} className={`streamlist-item ${item.completed ? 'completed' : ''}`}>
            {editIndex === index ? (
              <>
                <input
                  value={editValue}
                  onChange={handleEditChange}
                  className="edit-input"
                />
                <button onClick={() => handleSaveEdit(index)}><FaCheck /></button>
                <button onClick={() => setEditIndex(null)}><FaTimes /></button>
              </>
            ) : (
              <>
                <span>{item.text}</span>
                <button onClick={() => handleComplete(index)} title="Complete"><FaCheck /></button>
                <button onClick={() => handleEdit(index)} title="Edit"><FaEdit /></button>
                <button onClick={() => handleDelete(index)} title="Delete"><FaTrash /></button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StreamList;
